import { EntityStatus } from '@clmovies/shareds/utils/utils/functions';
import { createReducer, on } from '@ngrx/store';
import * as PokemonActions from '../actions/pokemon.actions';
export const pokemonFeatureKey = 'pokemon';
const initialState = {
  pokemons: [],
  status: EntityStatus.Initial,
  error: undefined
};
export const reducer = createReducer(initialState, on(PokemonActions.loadPokemon, state => {
  return Object.assign(Object.assign({}, state), {
    status: EntityStatus.Pending,
    error: undefined
  });
}), on(PokemonActions.loadPokemonSuccess, (state, {
  pokemons
}) => {
  return Object.assign(Object.assign({}, state), {
    pokemons,
    status: EntityStatus.Loaded
  });
}), on(PokemonActions.loadPokemonFailure, (state, {
  error,
  pokemons
}) => {
  return Object.assign(Object.assign({}, state), {
    pokemons,
    error,
    status: EntityStatus.Error
  });
}));
